export class ImmutableCollectionHelper {
  static removeItem(array, index) {
    return array.filter((item, idx) => idx !== index);
  }

  static updateObjectInArray(array, index, item) {
    return array.reduce((acc, elm, i) => i === index ? [...acc, Object.assign({}, item)] : [...acc, elm], []);
  }

}
