import { InjectionToken } from '@angular/core';
export const FEATURE_STORE_ANONYMIZER = new InjectionToken('FeatureStoreAnonymizer');
export class FeatureStoreAnonymizer {
  anonymize(state, injector) {
    const featureStateName = this.getFeatureStateName();
    const featureState = state[featureStateName];

    if (featureState) {
      this.anonymizeFeatureState(featureState, injector);
    } else {
      throw new Error(`Unable to find feature state at key "${featureStateName}" on RootState`);
    }
  }

}
